def longest_common_subsequence(s1, s2):
    # Initiate the array
    result = [[0 for x in range(len(s2) + 1)] for y in range(len(s1) + 1)]
    for x in range(len(s1) + 1):
        for y in range(len(s2) + 1):
            if x == 0 or y == 0:
                result[x][y] = 0
            elif s1[x - 1] == s2[y - 1]:
                result[x][y] = result[x - 1][y - 1] + 1
