#include <string>
#include <iostream>
#include <pandoc/pandoc.h>

std::string markdown_to_html(std::string markdown_text) {
    std::string html_text;
    pandoc_converter *converter = pandoc_new("markdown", "html5");
    pandoc_init(converter, NULL, 0, PANDOC_OPTION_NONE);
    pandoc_document *doc = pandoc_read_string(converter, markdown_text.c_str(), "UTF-8");
