    std::regex md_bold_regex("(\\*\\*)([^\\s]+)(\\*\\*)");
    std::regex md_italic_regex("(\\*)(.*?)(\\*)");
    std::string html;
    std::vector<std::string> words;
    std::vector<std::string> replacements;
