            words.push_back(md_bold_regex.str());
            replacements.push_back("<b>" + md_bold_regex.str(2) + "</b>");
        } else if (md_italic_regex.match(markdown_text, &word_count)) {
            words.push_back(md_italic_regex.str());
            replacements.push_back("<i>" + md_italic_regex.str(2) + "</i>");
