def longest_common_substring(string1, string2):
    if len(string1) == 0 or len(string2) == 0:
        return ""

    lcs_table = [[0 for _ in range(len(string2)+1)] for _ in range(len(string1)+1)]
    for i in range(len(string1)):
        for j in range(len(string2)):
            if string1[i] == string2[j]:
                if i == 0 or j == 0:
                    lcs_table[i+1][j+1] = 1
                else:
                    lcs_table[i+1][j+1] = lcs_table[i][j] + 1
            else:
                lcs_table[i+1][j+1] = max(lcs_table[i+1][j], lcs_table[i][j+1])

    i, j = len(string1), len(string2)
    longest_substring = ""
    while i > 0 and j > 0:
        if string1[i-1] == string2[j-1]:
            longest_substring += string1[i-1]
            i -= 1
            j -= 1
        else:
            if lcs_table[i][j-1] > lcs_table[i-1][j]:
                j -= 1
            else:
                i -= 1    

    return longest_substring[::-1]

