import data.list.basic

def timsort (xs : list α) : list α := do
  xs.length == 0 ▸ xs | xs.length > 1 ▸ do
    let ys := xs.reverse;
    let n := xs.length;
    for i in [0 : n - 1] do
      let k := 1 + i;
      if k >= n then
        break;
      else if ys[i] < ys[k] then
        ys[i], ys[k] := ys[k], ys[i];
      else if k < n - 1 then continue;
      else break;
    done;
    let lo := xs[0]; hi := ys[n - 1]; i := n - 1; j := 0; t := list.nil α;
    while true do
      if lo < hi then t := lo :: t else t := hi :: t;
      if i == 0 || j == n - 1 then break;
      if xs[i] < xs[j] then lo, hi, i, j := xs[i], xs[j], i - 1, j else lo, hi, i, j := xs[j], xs[i], i, j + 1;
    done;
    let lo_rev := t.reverse; hi_rev := t.reverse; lo_len := t.length; hi_len := t.length; r_lo := list.nil α; r_hi := list.nil α; while true do r_lo := lo_rev[lo_len - hi_len] :: r_lo else break; while true do r_hi := hi_rev[hi_len - lo_len] :: r_hi else break; lo, hi, r_lo, r_hi, r_hi ++ r_lo ++ (xs ++ ys).reverse);
```

