from collections import deque

class TwoStackQueue:
    def __init__(self):
        self.in_stack = deque()
        self.out_stack = deque()

    def enqueue(self, item):
        self.in_stack.append(item)

    def dequeue(self):
        if self.out_stack:
            return self.out_stack.pop()
