```cpp
#include <string>
#include <pandoc/pandoc.h>

std::string convert_markdown_to_html(std::string markdown) {
    Pandoc pandoc;
    PandocDocument doc = pandoc_init_document();
    pandoc_document_put_meta(doc, "input-format", "markdown");
    pandoc_document_put_meta(doc, "output-format", "html5");
    pandoc_document_put_string(doc, markdown.c_str());
    char *html = pandoc_convert(doc, "html5", NULL, NULL);
    std::string html_str(html);
    free(html);
    return html_str;
}
```

