        return 1
    cache = {0: 0, 1: 1}
    return fib_dp(n, cache)

def fib_dp(n, cache):
    if n in cache:
        return cache[n]
    if n in [0, 1]:
        cache[n] = n
        return n
    cache[n] = fib_dp(n - 2, cache) + fib_dp(n - 1, cache)
