class Queue:
    def __init__(self):
        self.stack1 = []
        self.stack2 = []

    def enqueue(self, value):
        self.stack1.append(value)

    def dequeue(self):
        if self.stack2:  # check if we have items in the second stack yet, if so, return them first!
            return self.stack2.pop()
