def lcs(x, y):
    m = len(x)
    n = len(y)

    # Create a matrix to store the results of subproblems
    dp = [[0 for _ in range(n + 1)] for _ in range(m + 1)]

    # Initialize the first row and first column with zeroes
    for i in range(1, m + 1):
        dp[i][0] = 0
    for j in range(1, n + 1):
        dp[0][j] = 0

    # Fill the matrix with dynamic programming values
