def longest_common_subsequence(seq1, seq2):
    if len(seq1) == 0 or len(seq2) == 0:
        return []

    lcs = {}
    for i in range(len(seq1)):
        for j in range(len(seq2)):
            if seq1[i] == seq2[j]:
                if (i, j) not in lcs:
                    lcs[(i, j)] = 1
                else:
                    lcs[(i, j)] += 1

    max_value = 0
    for value in lcs.values():
        if value > max_value:
            max_value = value

    result = []
    for (i, j), value in lcs.items():
        if value == max_value:
            result.append((i, j))

    return result[::-1]

