int binarySearch(int arr[], int l, int r, int key) {
    // Ha a tömbben nincs elem, -1 visszaadása
    if (l > r) {
        return -1;
    }
    // Középérték kiválasztása
    int mid = l + (r - l) / 2;
    // Ha az adott elem megegyezik a középértékkel, az adott elem indexének visszaadása
    if (arr[mid] == key) {
        return mid;
    } else if (arr[mid] > key) {
