std::string markdown_to_html(const std::string& markdown_text) {
    // HTML 태그를 일치시키는 정규식 패턴
    const std::regex html_tag_pattern(R"(<[^>]+>)");

    // Markdown 언어에서 HTML로의 일반적인 변환을 수행합니다.
    std::string html_text = markdown_text;
    html_text = std::regex_replace(html_text, html_tag_pattern, "");
    html_text = replaceAll("*", "<b>", html_text);
    html_text = replaceAll("_", "<i>", html_text);
    html_text = replaceAll("`", "<code>", html_text);
    html_text = replaceAll("##", "<h2>", html_text);
    html_text = replaceAll("#", "<h1>", html_text);
    html_text = replaceAll("###", "<h3>", html_text);
    html_text = replaceAll("####", "<h4>", html_text);
    html_text = replaceAll("#####", "<h5>", html_text);
    html_text = replaceAll("######", "<h6>", html_text);
    html_text = replaceAll("- ", "<li>", html_text);
    return std::move(html_text);
}
```

