using namespace std;

int main() {
    // Declare and initialize a string variable to store the input text.
    string text = "The quick brown fox jumps over the lazy dog";

    // Read the input text from the user.
    cout << "Enter the text you want to process: ";
    cin >> text;

    // Count the number of times each word appears in the input text.
    int word_count[256]; // Allocate an array of size 256 to store the word counts.
    for (int i = 0; i < text.size(); i++) { // Loop through each character in the input text.
        word_count[text[i]]++; // Increment the word count for each character that is part of a known word.
    }

    // Sort the word counts by frequency, from highest to lowest.
    for (int i = 0; i < text.size(); i++) { // Loop through each character in the input text again, but this time starting from the beginning and ending at the end of the string.
        if (word_count[text[i]] > word_count[text[i+1]]) { // If one character has a higher word count than another character, swap them.
            swap(word_count[text[i]], word_count[text[i+1]]); // Swap their values in memory.
        }
    }

    // Print out the top ten words in order of frequency, along with their counts.
    for (int i = 0; i < 10; i++) { // Print out the top ten words in order of frequency, along with their counts.
