using namespace std;

int main() {
    string mot_cherche = "cherche"; // mot à rechercher dans le fichier
    string fichier = "fichier.txt"; // nom du fichier
    int occurrences = 0; // nombre d'occurrences trouvées du mot cherche dans le fichier
    ifstream file(fichier); // ouvrir le fichier en lecture seule
    if (!file) { // si l'ouverture du fichier a échoué
        cout << "Impossible d'ouvrir le fichier" << endl;
        return -1;
    } else { // sinon, lire ligne après ligne depuis le fichier en fonction des caractères qu'il contient, jusqu'à la fin du fichier inclus. 
