    for (auto& line : markdownText) {
        if (line == '\n') {
            htmlText.push_back('<br />');
        } else if (line == '`') {
            htmlText.push_back('<code>');
        } else if (line == '*' || line == '-') {
            htmlText.push_back('<span class="text-danger">');
        } else if (line == '_') {
            htmlText.push_back('<span class="text-secondary">');
        } else if (line == '>') {
            htmlText.push_back('</span>');
        } else if (line == '~' || line == '^' || line == '|') {
            htmlText.push_back('<span class="text-muted">');
        } else {
