```lean
fun timsort(s: string): string {
  var i, j: nat;
  var pivot: char;
  for i in 0..s.len do {
    pivot := s[i];
    for j in i + 1..s.len do {
      if s[j] < pivot then {
        swap(s[i], s[j]);
        i := i + 1;
      } else if s[j] > pivot then {
        swap(s[i], s[j]);
        j := j - 1;
      } else { break }
    }
    if s[i] < pivot then { break } else if s[i] > pivot then { break }
  }
  return s;
}
```
