```cpp
std::string to_html(const std::string &markdown) {
    // Prepare markdown string
    std::string markdown_string = markdown;

    // Convert markdown to html string
    std::string html_string;
    for (size_t pos = 0; pos < markdown_string.length(); pos++) {
        char c = markdown_string[pos];
        switch (c) {
            case '*':
            case '+':
                html_string += "<strong>";
                break;
            case '-':
                html_string += "<em>";
                break;
            case '>':
                html_string += "<sup>";
                break;
            case '_':
                html_string += "<sub>";
                break;
            case '`':
                html_string += "<code>";
                break;
            default: // If not part of the list above, leave it as is.
        }
    }
