def timsort[A: Ord: HasNull](xs: List[A]): List[A] {
  if xs.size == 0 { return xs }
  def partition(xs: List[A], low: Int, high: Int): (Int, List[A]) {
    val pivot = xs(high)
    var index = low
    for i <- low to high do {
      if xs(i) <= pivot {
        index += 1
        xs.swap(i, index)
      } else { break }
    }
    val pivotIndex = index - 1
    val (low, high) = if pivot < pivotIndex then (index, pivotIndex) else (pivotIndex, index)
    return (low, xs.slice(low, high))
