            self.rear = new_element
        self.size += 1
    def dequeue(self):
        if not self.is_empty():
            front_node = self.front # save front node for later use
            self.front = front_node.next # move front node to rear (swap nodes)
            if not self.rear: # if no more nodes in the queue, set rear to front node (swap nodes again)
                self.rear = front_node # reset rear to the first node in the queue (now it's the new front node)
            else: # otherwise set rear to the next node in the queue (move next node to rear position)
                self.rear = self.rear.next # move rear to next node (swap nodes again)
            del front_node # delete old front node (no longer needed)
            return self.front.value # return value from old front node (now re-assigned to new front node) 
    def is_empty(self): # return True if queue is empty, False otherwise 
