#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int binary_search(int* array, int size, int target) {
    int start = 0;
    int end = size - 1;
    int mid = 0;
    while (start <= end) {
        mid = start + (end - start) / 2;
        if (array[mid] == target) {
            return mid;
        } else if (array[mid] > target) {
            start = mid + 1;
        } else {
            end = mid - 1;
        }
    }
    return -1; // Target not found
}
```
