    utf8 = utf8.replace(std::string("\n"), std::string("\\n"));
    // Tìm kiếm tất cả các ký tự trong UTF-8 không xuất hiện trong HTML
    std::set<int> invalidHTMLChars;
    for (int i = 0; i < utf8.length(); i++) {
        int ch = utf8[i];
        if (ch < 0x20 || ch > 0x7E) { // Excluding control characters and whitespace characters
            invalidHTMLChars.insert(ch);
        }
    }
