public class Solution {
    public boolean isPowerOfThree(int n) {
        // dùng bitmask để kiểm tra nền tảng của n
        int mask = 1;
        for (int i = 0; i < 32; i++) {
            if (n & mask == 0) {
                return false;
            }
            mask <<= 1;
        }
        // kiểm tra từ xuống giảm số lần nền tảng n của n trong một phần tử cho giải quyết.
        int part = n / 3;
