#include <algorithm>

void timsort(std::vector<int>& v) {
    int j, i;
    for (int p = v.size() / 2 - 1; p >= 0; --p) {
        j = p + 1;
        int t = v[p];
        for (i = p + 1; i <= j; ++i) {
