```cpp
#include <string>
#include <iostream>
#include <regex>

std::string markdownToHtml(const std::string& input) {
    // Regex pattern to match markdown syntax: 
    //     # Heading 1 (H1)
    //     ## Heading 2 (H2)
    //     ### Heading 3 (H3)
    //     #### Heading 4 (H4)
    //     ##### Heading 5 (H5)
    //     ###### Heading 6 (H6)
    //     {code}
    //     {raw}
    //     [link](url "optional title")
    //     [link](url "optional title" target="_blank")
    //     [link](url "optional title" target="_self")
    //     [link](url "optional title" target="_parent")
    std::regex reg("\\{([a-zA-Z]+)\\}(?:\\[(.+?)(?: [^}]+)?\\])?\\{([a-zA-Z]+)\\}(?:\\[(.+?)(?: [^}]+)?\\])?");
    std::smatch match; 
    if(std::regex_match(input, match, reg)){
        return "<h" + std::to_string(match[1].str().size()) + ">" + match[1].str() + "</h" + std::to_string(match[1].str().size()) + ">\n";  
        for(int i=2; i<=match.size(); ++i){  
            if(i == match.size()){  
                return "<p>" + match[i].str() + "</p>\n";  
            }else{  
                return "<h" + std::to_string(match[i].str().size()) + ">" + match[i].str() + "</h" + std::to_string(match[i].str().size()) + ">\n";  
            }  
        }  
        return "<p>" + match[match.size()].str() + "</p>\n";  
    }else{  
        return "<p>" + input + "</p>\n";  
    }  
}

