def find_longest_common_subsequence(s1, s2):
    """Return the length of the longest common subsequence of two strings."""
    n = max(len(s1), len(s2))
    dp = [[0 for _ in range(n)] for _ in range(n)]

    for i in range(n):
        for j in range(n):
            if i == 0 or j == 0:
