std::string convertMarkdownToHtml(std::string& markdownText) {
    std::string htmlText;
    // Parse the Markdown text into an HTML fragment using a Markdown parser.
    // Note that the Markdown parser may have its own set of quirks and limitations.
    // In this example, we'll use a simple implementation of a Markdown parser.
    // This function can be customized to use any Markdown parser that meets your needs.
    for (int i = 0; i < markdownText.length(); i++) {
        if (markdownText[i] == '>') {
            htmlText += "<p>";
        } else if (markdownText[i] == '<') {
            htmlText += "<span style=\"color: red;\">";
        } else if (markdownText[i] == ' ') {
            htmlText += "<br />";
        } else {
            htmlText += markdownText[i];
        }
    }
    // Return the HTML fragment as a string.
    return htmlText;
}
