import collections

def find_longest_common_subsequence(s1, s2):
    # Initialize two dictionaries: dp[i][j] stores the length of the longest common subsequence between s1[i:j+1] and s2.
    # Also, dp[i][j] == -1 if there is no common subsequence between s1[i:j+1] and s2.
    dp = collections.defaultdict(list)
    for i in range(len(s1)):
        for j in range(len(s2)):
            # If there is a common subsequence between s1[i:j+1] and s2, then we can use it as a subsequence of the common subsequence.
            if dp[i][j] == -1 or dp[i][j][0] != i:
                # Find the length of the longest common subsequence between s1[i:j+1] and s2.
                length = 0
                while i + length <= j:
                    # If the current character in s1 is equal to the current character in s2, then we have found a new common subsequence.
                    if s1[i+length] == s2[j-length]:
                        # Update the length of the longest common subsequence.
                        length += 1
                        # Store the longest common subsequence as a new element in the list of lengths at (i, j).
                        dp[i][j].append(length)
                    # If the current character in s1 is not equal to the current character in s2, then we don't have a new common subsequence. Break out of the loop.
                    else:
                        break
                # If we didn't find a new common subsequence, then update dp[i][j] to -1.
                if i + length >= j:
                    dp[i][j] = -1
            else:
                length = min(dp[i][j], dp[i+1][j]) + 1  # Update dp[i][j] with minimum of current and previous lengths. This will be used for finding longer subsequences later.  # Note that this could cause an error if you don't take care of it by modifying it before calling this function!

