```cpp
std::string html_format(const std::string& text) {
  // Split the text into individual lines
  std::vector<std::string> lines = text.split("\n");

  // Create a new string to store the HTML-formatted text
  std::string html = "<html><body>";

  // Loop through each line in the text and add an HTML tag to each line
  for (const auto& line : lines) {
    // Add a <p> tag to each line of plain text
    html += "<p>" + line + "</p>";
  }

  // Close the <body> tag and the </html> tag at the end of the string
  html += "</body></html>";

  return html;
}
