#include <string>
#include <sstream>
#include <iostream>
#include <ctype.h>

std::string markdown_to_html(std::string markdown) {
    std::string html = "";
    std::istringstream ss(markdown);
    std::string token;
    while (std::getline(ss, token, '\n')) {
        if (token == "") continue;
        html += "<p>";
        html += token;
        html += "</p>\n";
    }
    return html;
}
