def email_validator(email_address: str) -> bool:
    # Validate the length of the email address
    if len(email_address) < 1 or len(email_address) > 255:
        return False

    # Split the email address into its component parts
    email_parts = email_address.split("@", 1)

    # Validate the format of the email address's domain name
    if len(email_parts[1]) > 255 or not re.match(r"^[a-z0-9\-]+(\.[a-z0-9\-]+)*\.([a-z]{2,4})$", email_parts[1]):
