            if (current_word[i] == ' ') { // If current character is a space, update next word to be the previous word.
                next_word = next_word + " "; // Join words with spaces between them.
            } else if (current_word[i] == '"') { // If current character is a quote character, update next word to be the previous word surrounded by quotes.
                next_word = next_word + "\""; // Join words with quotes around them.
            } else if (current_word[i] == '\\') { // If current character is a backslash character, update next word to be the previous word with a backslash in front of it.
                next_word = next_word + "\\"; // Join words with backslashes between them.
            } else { // Otherwise, add current character to the end of the current word and update next word to be the previous word with no changes made to it.
                next_word += current_word[i]; // Join words together without changing them.
