def common_substring(sentence1, sentence2):
    """
    Tekintettel a dinamikus programozásra, megtalálja a két bemeneti karakterlánc leghosszabb közös részsorozatát.
    """
    common_substring_length = 0
    for i in range(len(sentence1)):
        for j in range(len(sentence2)):
            if sentence1[i] == sentence2[j]:
                common_substring_length += 1
