```
#include <iostream>

int fib(int n) {
    if (n == 1 || n == 2) {
        return n;
    } else {
        return fib(n - 1) + fib(n - 2);
    }
}

int main() {
    int n;
    std::cout << "Adja meg a Fibonacci számot: ";
    std::cin >> n;
    std::cout << "Az n-edik Fibonacci szám: " << fib(n) << std::endl;
    return 0;
}
