// Function to check if an array of integers is a valid prime number
function checkPrime(arr) {
    for (let i = 0; i < arr.length; i++) {
        for (let j = 2; j < arr[i]; j++) {
            if (arr[i] % j === 0) {
                return false;
            }
        }
    }
    return true;
}
