class Dequeue:
    def __init__(self, max_size):
        self.max_size = max_size
        self.stack = [None] * self.max_size

    def push(self, value):
        self.stack[0] = value

    def pop(self):
        if self.stack[0] is None:
            raise IndexError("Dequeue is empty")
        else:
            return self.stack.pop(0)

    def is_empty(self):
        if self.stack[0] is None:
            return True
        else:
