int findElement(int a[], int size, int target) {
	int low = 0;
	int high = size - 1;
	int mid;
	while (low <= high) {
		mid = low + (high - low) / 2; // формиране на преградите
		if (a[mid] == target) { // оценяване на преградите
			return mid; // връщане на индекса на открития елемент или -1, ако такава не е намерена
		} else if (a[mid] < target) { // ако откритият елемент е по-малък от заявката, ще преминем към низходни прегради, ако това не е случая, ще преминем към горни прегради
			if (a[mid] == target) return mid; // връщане на индекса на открития елемент или -1, ако такава не е намерена
		} else { // ако откритият елемент е по-голям от заявката, ще преминем към горни прегради, ако това не е случая, ще преминем към низходни прегради
			if (a[mid] == target) return mid; // връщане на индекса на открития елемент или -1, ако такава не е намерена
		}
