    // Parse markdown to html
    for (const auto &s : markdown) {
        if (s == ">") {
            html += "<strong>";
        } else if (s == "<") {
            html += "<span class=\"markdown\">";
        } else if (s == "</") {
            html += "</span>";
        } else if (s == "\n") {
            html += "<br/>";
