#! /usr/bin/env python3

from typing import List
from collections import Counter

def longest_sequence(strings: List[str]) -> int:
    # Initialization
    longest_sequence = -1
    count = Counter()
    for string in strings:
        for word in string.split():
            if word not in count:
                count[word] = 1
            else:
                count[word] += 1
    # Traverse through all possible sequences and find longest sequence.
    for i in range(len(strings)):
        seq = ''
        for j in range(i, len(strings)):
            seq += strings[j]
        if count[seq] > longest_sequence:
            longest_sequence = count[seq]
    return longest_sequence + 1

