class Queue(object):
    def __init__(self, size=0):
        self._data = [None] * size
        self._head = 0
        self._tail = 0

    def enqueue(self, value):
        if self._tail == len(self._data) - 1:
            self._tail += 1
            self._data.append(value)
        else:
            self._tail += 1
            self._data[self._tail] = value
        return self._tail

    def dequeue(self):
        if self._head == self._tail:
            return None
        else:
            value = self._data[self._head]
            self._head += 1
            return value
```
