    def __init__(self, song):
        self.song = song
    def play(self):
        print("Playing: " + self.song)
    def pause(self):
        print("Pausing")
    def stop(self):
        print("Stopping")
    def next(self):
        if self.song == None:
            return self.play()
        else:
            self.song = None # clear the current song variable to make sure we don't play the same song twice in a row.
            return self.play() # play the next song in the queue.
    def prev(self):
        if self.song == None:
            return self.play() # play the first song in the queue if there's no current song playing.
        else:
            return self.stop() # stop the current song playing and play the previous one in the queue.
