public static boolean isPowerOfThree(int n) {
    if (n < 0 || n > 231 - 1) {
        return false;
    }

    int low = 0;
    int high = n;

    while (low <= high) {
        int mid = low + (high - low) / 2;
        if (mid * mid == n) {
            return true;
        } else if (mid * mid < n) {
            low = mid + 1;
        } else {
