            word_count[word]++;
        }
        // 输出统计结果。
        for (const auto &word : word_count) {
            std::cout << word.first << ":" << word.second << '\n';
        }
    }
}
```

实现使用两个栈的队列数据结构的可以使用如下代码：
```
class MyQueue:
    def __init__(self):
        self.front = []
        self.back = []
    def enqueue(self, data):
        if not self.is_full():
            self.back.append(data)
            self.back.append(None)
            self.front.append(None)
    def dequeue(self):
        if self.is_empty():
            return None
        data = self.back[0]
        self.back[0] = self.back[1]
        self.back[1] = None
        return data
    def is_empty(self):
        return len(self.front) == 0
    def is_full(self):
        return len(self.back) == 2
```

