def timsort(xs : List[Int]): List[Int] =
  def partition(xs : List[Int], pivot : Int) : List[Int] =
    def aux(i : Int, j : Int, pivot : Int) : List[Int] =
      if i > j then
        (i, j) match
          | (i, j) if xs[j] >= pivot && xs[i] <= pivot => aux(i + 1, j, pivot)
          | (i, j) if xs[j] <= pivot && xs[i] >= pivot => aux(i + 1, j - 1, pivot)
          | _ => (i, j)
      else
        (i, j) match
          | (i, j) if xs[j] >= pivot && xs[i] <= pivot => aux(i + 1, j - 1, pivot)
          | (i, j) if xs[j] <= pivot && xs[i] >= pivot => aux(i + 1, j, pivot)
          | _ => (i, j)
    return aux(0, len(xs), pivot)

    def insertion_sort(xs : List[Int], i : Int): List[Int] =
      while i < len(xs):
        if i == 0 then return xs
        else if xs[i - 1] > xs[i]: return insertion_sort(xs.slice(0, i), i - 1); return insertion_sort(xs.slice(0, i), i + 1);

    def merge_sort(xs : List[Int], l : Int, r : Int): List[Int] =
