def search_bit_in_binary_mask(mask, position):
    """Searches for the bit specified in the position of the given mask"""
    if mask is None:
        return None
    if position < 0 or position > len(mask):
        return None
    if not isinstance(mask, bytes):
        mask = binascii.a2b_hex(mask)
    if not isinstance(position, int):
        position = ord(position)
