def longest_common_substring(s1, s2):
    n = len(s1)
    m = len(s2)
    dp = [[0] * (n + 1) for _ in range(m + 1)]

    # 初始化状态 dp[i][j] = 0表示字符串i和j是没有公共子序列的
    for i in range(n + 1):
        dp[i][0] = i
    for j in range(m + 1):
        dp[0][j] = j

    # 计算字符串i和j的公共子序列长度
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if s1[i - 1] == s2[j - 1]:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1]) + 1
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])
    return dp[-1][-1]
