    // Regex pattern for replacing markdown code blocks:
    // https://stackoverflow.com/a/34107656/14795093
    std::regex pattern("```([^\\n]+?)(?:\\n(.+?)(?:^[^\\n])*)\\n```");
    std::smatch match;

    // Replacing markdown code blocks with html entities:
    string html_text = "";
    for (const auto& char_ : markdown_text) {
