#include <iostream>
#include <string>
#include <vector>

int main() {
    std::string s("How many words are there in the text?");
    std::vector<std::string> words;
    std::string word;
    std::size_t num_words = s.size();
    for (std::size_t i = 0; i < num_words; ++i) {
        word = s[i];
        if (word == " ") {
            continue;
        } else {
            words.push_back(word);
        }
    }
    for (const auto &word : words) {
        std::cout << word << " ";
    }
}
