        // Løkken sorteres i ændring af et foreslået element i løkken og derefter foreslået element placeres i listen
        var p : int = l[next]
        next += 1
        while (current < next) {
            if (l[current] < p) {
                result.push(l[current])
                current += 1
            } else {
                result.push(p)
                p = l[next]
                next += 1
            }
        }
        result.push(p)
        current += 1
    }
    return result
}

